import unittest
from collections import defaultdict

class Solution:
    def is_permutation(self, m1, s:str) -> bool:
        m2 = defaultdict(int)
        for c in s:
            m2[c] += 1
        for k in m1.keys():
            if m1[k] != m2[k]:
                return False
        return True

    def checkInclusion(self, s1: str, s2: str) -> bool:
        m1 = defaultdict(int)
        for c in s1:
            m1[c] += 1
        left = 0
        while left <= len(s2) - len(s1):
            right = left + len(s1)
            if self.is_permutation(m1, s2[left:right]):
                return True
            left += 1
        return False

s = Solution()

class TestSolution(unittest.TestCase):
    def test1(self):
        s1 = "ab"
        s2 = "eidbaooo"
        self.assertTrue(s.checkInclusion(s1, s2))

    def test2(self):
        s1 = "ab"
        s2 = "eidboaoo"
        self.assertFalse(s.checkInclusion(s1, s2))

    def test3(self):
        s1 = "ooda"
        s2 = "eidboaod"
        self.assertTrue(s.checkInclusion(s1, s2))

    def test9(self):
        s1 = ""
        s2 = ""
        self.assertTrue(s.checkInclusion(s1, s2))

if __name__ == '__main__':
    unittest.main()        
